import React, { useState, useEffect } from 'react'


// import images ðŸ“¸
import lvlogo from '../assets/Little_Vectors_logo.png';

// import styles ðŸŽ¨
import '../styles/Header.css';

// import components
import './Games';
import './ContactForm';
import './Infographics';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  // Use useEffect to check screen size on mount and resize
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth > 768) {
        // Close the menu if screen size is larger than 768px
        setIsMenuOpen(false);
      }
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <div>
      <div className='header'>
        <div className='lvlogoheader'>
          <a href="./">
            <img className='LvLogoImg' alt="Little Vectors" src={lvlogo} />
          </a>
        </div>
        <div className={`navbar ${isMenuOpen ? 'open' : ''}`}>
        <button onClick={toggleMenu} className='menu-button'>
          &#9776;
        </button>
        <ul>
          <li><a href="./Games">Games</a></li>
          <li><a href="./Infographics">Infographics</a></li>
          <li><a href="./ContactForm">Contact</a></li>
        </ul>
      </div>
    </div>


    </div >
  )
}

export default Header
