import React, {useState} from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

// import styles ðŸŽ¨
import '../styles/contact.css';

const ContactForm = () => {
    const [contact, setContact] =useState({
        firstName: "", 
        lastName: "",
        email: "",
        learn: ""
    });

    const onChangeHandler = (e) => {
        console.log(e)
        setContact({
          ...contact,
          [e.target.name]: e.target.value
        })
      }

    const navigate = useNavigate();

    const onSubmitHandler = (e) => {
      e.preventDefault();
      //prevent default behavior of the submit
      //make a post request to create a new contact
      console.log("---------", contact)
      axios.post('http://localhost:8000/api contact/create', contact)
      // this is the same as the route in the contact.routes is this ok?
        .then(res => {
          console.log("This is the response", res);
          console.log("This is response.data", res.data);
          navigate('/thankYou')
        })
        .catch((err) => {
          console.log(err);
        })
    }
    return (
        <div>
            <form onSubmit={onSubmitHandler}>
                <div className="">
                    <label htmlFor="firstName">First Name:</label><br />
                    <input type="text" name="firstName" id="firstName" className="" onChange = {onChangeHandler}/>
                    <label htmlFor="lastName">Last Name:</label><br />
                    <input type="text" name="lastName" id="lastName" className="" onChange = {onChangeHandler}/>
                </div>
                <div className="">
                    <label htmlFor="email">email:</label><br />
                    <input type="text" name="email" id="email" className="" onChange = {onChangeHandler}/>
                </div>


                <div className="">
                    <label htmlFor="learn">What would you like to learn about?</label><br />
                    <input type="text" name="learn" id="learn" className="" onChange = {onChangeHandler}/>
                </div>
                <button>Submit</button>
            </form>

        </div>
    )
}

export default ContactForm

